// Funci√≥n para convertir n√∫meros seriales de Excel a fechas
function convertExcelDate(excelDate) {
  if (!excelDate || excelDate === '') return null;

  // Si es un n√∫mero (fecha serial de Excel)
  if (!isNaN(excelDate) && typeof excelDate === 'number') {
    const millisecondsPerDay = 24 * 60 * 60 * 1000;
    const excelEpoch = new Date(1899, 11, 30); // 30 de diciembre de 1899
    const jsDate = new Date(excelEpoch.getTime() + excelDate * millisecondsPerDay);

    // Formatear manualmente para evitar problemas de zona horaria
    const day = jsDate.getUTCDate().toString().padStart(2, '0');
    const month = (jsDate.getUTCMonth() + 1).toString().padStart(2, '0');
    const year = jsDate.getUTCFullYear();

    return `${day}/${month}/${year}`;
  }

  // Si es un string que parece un n√∫mero
  if (!isNaN(parseFloat(excelDate)) && isFinite(excelDate)) {
    const numDate = parseFloat(excelDate);
    const millisecondsPerDay = 24 * 60 * 60 * 1000;
    const excelEpoch = new Date(1899, 11, 30);
    const jsDate = new Date(excelEpoch.getTime() + numDate * millisecondsPerDay);

    const day = jsDate.getUTCDate().toString().padStart(2, '0');
    const month = (jsDate.getUTCMonth() + 1).toString().padStart(2, '0');
    const year = jsDate.getUTCFullYear();

    return `${day}/${month}/${year}`;
  }

  // Si ya es una fecha en formato string, la retornamos tal como est√°
  return excelDate;
}

