// Funci√≥n de b√∫squeda mejorada
function buscarCooperativa(datos, nombreBuscado) {
  if (!nombreBuscado) return [];

  const searchTerm = nombreBuscado.toLowerCase();

  return datos.filter(row => {
    if (!row.json.COOPERATIVA) return false;

    const coopName = row.json.COOPERATIVA.toLowerCase();

    // B√∫squeda exacta primero
    if (coopName === searchTerm) return true;

    // Luego b√∫squeda por palabras clave (excluyendo palabras comunes)
    const palabrasClaves = searchTerm
      .replace(/cooperativa\s+(de\s+)?ahorro\s+y\s+cr[e√©]dito\s*/gi, '')
      .replace(/cooperativa\s*/gi, '')
      .trim()
      .split(/\s+/)
      .filter(word => word.length > 2); // Solo palabras de m√°s de 2 caracteres

    if (palabrasClaves.length === 0) return false;

    // Verificar que todas las palabras clave est√©n en el nombre
    return palabrasClaves.every(palabra => coopName.includes(palabra));
  });
}

// Funci√≥n para buscar por criterios espec√≠ficos
function buscarPorCriterio(datos, criterio, valor) {
  if (!valor) return [];

  const valorBusqueda = valor.toLowerCase();

  return datos.filter(row => {
    if (!row.json) return false;

    const valorCampo = row.json[criterio];
    if (!valorCampo) return false;

    return valorCampo.toLowerCase().includes(valorBusqueda);
  });
}

