// Funci√≥n para limitar el tama√±o de la respuesta a menos de 4096 caracteres
function limitarRespuesta(respuesta, maxCaracteres = 4000) {
  if (respuesta.length <= maxCaracteres) {
    return respuesta;
  }

  // Cortar la respuesta y a√±adir mensaje de truncamiento
  const respuestaCorta = respuesta.substring(0, maxCaracteres - 100);
  const ultimoSalto = respuestaCorta.lastIndexOf('\n');

  // Cortar en el √∫ltimo salto de l√≠nea para no cortar una l√≠nea a la mitad
  const respuestaFinal = ultimoSalto > 0 ? respuestaCorta.substring(0, ultimoSalto) : respuestaCorta;

  return respuestaFinal + `\n\n‚ö†Ô∏è *Resultados limitados debido al l√≠mite de caracteres*\nüìù Se mostraron los primeros resultados`;
}

